class help:
        
        
    def Youtube_Download_video_Resolution_help(self):
        print(f"Usage: <downloder> youtube video -get_resolution [Options]")
        print()
        print("Description :: ")
        print("In this command is used to show's Resolution of Youtube Video")
        print()
        print("Options  are :")
        print()
        print("positional arguments : ")
        print("   -get_resolution       Show's the Resolution of the Youtube")
        print()
        print("Optional arguments : ")
        print("-h, --help   show this help message and exit")
        
        
