def check_strange(name):
    chars = set('() -')
    return any((c in chars) for c in name)


def get_aliases(name, last_name):
    aliases = [name + last_name, name[0:1] + last_name, name[0:1] + '.' + last_name]

    return aliases


def create_email(name, last_name, domain):
    name = replace_other_marks(name.lower())
    last_name = replace_other_marks(last_name.lower())
    if not check_strange(name) or check_strange(last_name):
        email_provider = DhostingProvider()
        email = f'{name}.{last_name}@{domain}'
        pwd = email_provider.generate_password(12)
        mail = email_provider.create_mailbox(email, pwd)

        for alias in get_aliases(name, last_name):
            mail.create_alias(f'{alias}@{domain}')
    else:
        raise DisallowedMarks

    return mail, pwd

